package com.fidilaundry.app.ui.authimport android.content.Intentimport android.os.Bundleimport com.fidilaundry.app.Rimport com.fidilaundry.app.basearch.localpref.PaperPrefsimport com.fidilaundry.app.basearch.viewmodel.LoginViewModelimport com.fidilaundry.app.basearch.viewmodel.RegisterViewModelimport com.fidilaundry.app.databinding.ActivityLoginBindingimport com.fidilaundry.app.ui.MainActivityimport com.fidilaundry.app.ui.base.BaseActivityimport com.fidilaundry.app.util.LoadingDialogimport com.fidilaundry.app.util.setSafeOnClickListenerimport org.koin.androidx.viewmodel.ext.android.getViewModelclass LoginActivity : BaseActivity() {    lateinit var paperPrefs: PaperPrefs    lateinit var loadingDialog: LoadingDialog    private val binding: ActivityLoginBinding by binding(R.layout.activity_login)    private val viewModel: LoginViewModel by lazy {        getViewModel(LoginViewModel::class)    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        binding.apply {            lifecycleOwner = this@LoginActivity            this.vm = viewModel        }        binding.btnRegister.setSafeOnClickListener {            intent = Intent(this@LoginActivity, RegisterActivity::class.java)            startActivity(intent)        }        binding.btnLogin.setSafeOnClickListener {            intent = Intent(this@LoginActivity, MainActivity::class.java)            startActivity(intent)        }    }}