package com.fidilaundry.app.ui.authimport android.os.Bundleimport com.fidilaundry.app.Rimport com.fidilaundry.app.basearch.localpref.PaperPrefsimport com.fidilaundry.app.basearch.viewmodel.RegisterViewModelimport com.fidilaundry.app.databinding.ActivityRegisterBindingimport com.fidilaundry.app.ui.base.BaseActivityimport com.fidilaundry.app.util.LoadingDialogimport org.koin.androidx.viewmodel.ext.android.getViewModelclass RegisterActivity : BaseActivity() {    lateinit var paperPrefs: PaperPrefs    lateinit var loadingDialog: LoadingDialog    private val binding: ActivityRegisterBinding by binding(R.layout.activity_register)    private val viewModel: RegisterViewModel by lazy {        getViewModel(RegisterViewModel::class)    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        binding.apply {            lifecycleOwner = this@RegisterActivity            this.vm = viewModel        }    }}