package com.fidilaundry.app.ui.complaintimport android.Manifestimport android.app.Activityimport android.app.AlertDialogimport android.content.Intentimport android.content.pm.PackageManagerimport android.graphics.BitmapFactoryimport android.net.Uriimport android.os.Buildimport android.os.Bundleimport android.provider.MediaStoreimport android.view.Viewimport android.widget.RelativeLayoutimport androidx.core.app.ActivityCompatimport androidx.core.content.ContextCompatimport androidx.core.content.FileProviderimport androidx.core.content.res.ResourcesCompatimport com.bumptech.glide.Glideimport com.bumptech.glide.load.engine.DiskCacheStrategyimport com.fidilaundry.app.BuildConfigimport com.fidilaundry.app.Rimport com.fidilaundry.app.basearch.localpref.PaperPrefsimport com.fidilaundry.app.basearch.viewmodel.HomeViewModelimport com.fidilaundry.app.databinding.ActivityComplaintBindingimport com.fidilaundry.app.databinding.ActivityUserComplaintBindingimport com.fidilaundry.app.ui.base.BaseActivityimport com.fidilaundry.app.util.FontTextViewimport com.fidilaundry.app.util.ImageUtilsimport com.fidilaundry.app.util.LoadingDialogimport com.fidilaundry.app.util.fdialog.ConfirmMessageimport com.fidilaundry.app.util.fdialog.FGCallbackimport com.fidilaundry.app.util.setSafeOnClickListenerimport org.koin.androidx.viewmodel.ext.android.getViewModelimport java.io.Fileclass ComplaintActivity : BaseActivity() {    lateinit var paperPrefs: PaperPrefs    lateinit var loadingDialog: LoadingDialog    private val binding: ActivityComplaintBinding by binding(R.layout.activity_complaint)    private val viewModel: HomeViewModel by lazy {        getViewModel(HomeViewModel::class)    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        binding.apply {            lifecycleOwner = this@ComplaintActivity            this.vm = viewModel        }        binding.ivBack.setSafeOnClickListener {            finish()        }        binding.llInrogress.setSafeOnClickListener {            binding.vp.currentItem = 0            inprogressActive()        }        binding.llComplete.setSafeOnClickListener {            binding.vp.currentItem = 1            completeActive()        }    }    private fun completeActive() {        inactiveLayout(binding.lineInprogress, binding.tvInprogress)        activeLayout(binding.lineComplete, binding.tvComplete)    }    private fun inprogressActive() {        activeLayout(binding.lineInprogress, binding.tvInprogress)        inactiveLayout(binding.lineComplete, binding.tvComplete)    }    private fun inactiveLayout(rl: RelativeLayout, tv: FontTextView) {        rl.background = null        tv.setTextColor(            ResourcesCompat.getColor(resources, R.color.grey_text, null)        )    }    private fun activeLayout(rl: RelativeLayout, tv: FontTextView) {        rl.background = ContextCompat.getDrawable(this, R.drawable.bg_tab_line)        tv.setTextColor(ResourcesCompat.getColor(resources, R.color.blackHistory, null))    }}