package com.fidilaundry.app.ui.home.masterimport android.os.Bundleimport com.fidilaundry.app.Rimport com.fidilaundry.app.basearch.localpref.PaperPrefsimport com.fidilaundry.app.basearch.viewmodel.HomeViewModelimport com.fidilaundry.app.databinding.ActivityAddPriceBindingimport com.fidilaundry.app.ui.base.BaseActivityimport com.fidilaundry.app.util.LoadingDialogimport com.fidilaundry.app.util.fdialog.ConfirmMessageimport com.fidilaundry.app.util.fdialog.FGCallbackimport com.fidilaundry.app.util.setSafeOnClickListenerimport org.koin.androidx.viewmodel.ext.android.getViewModelclass AddPriceActivity : BaseActivity() {    lateinit var paperPrefs: PaperPrefs    lateinit var loadingDialog: LoadingDialog    private val binding: ActivityAddPriceBinding by binding(R.layout.activity_add_price)    private val viewModel: HomeViewModel by lazy {        getViewModel(HomeViewModel::class)    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        binding.apply {            lifecycleOwner = this@AddPriceActivity            this.vm = viewModel        }        var type = intent.getStringExtra("type")        when (type) {            "1" -> binding.tvTitle.text = "Tambah Harga"            "2" -> binding.tvTitle.text = "Ubah Harga"        }        binding.ivBack.setSafeOnClickListener {            finish()        }        binding.btnDelete.setSafeOnClickListener {            confirmDelete()        }    }    private fun confirmDelete() {        ConfirmMessage(            this,            "Apakah Anda yakin menghapus data ini?",            "", "",            "Hapus", "Batal",            object : FGCallback {                override fun onCallback() {                }            }        )    }}