package com.fidilaundry.app.ui.home.masterimport android.os.Bundleimport com.fidilaundry.app.Rimport com.fidilaundry.app.basearch.localpref.PaperPrefsimport com.fidilaundry.app.basearch.viewmodel.HomeViewModelimport com.fidilaundry.app.databinding.ActivityAddUserBindingimport com.fidilaundry.app.ui.base.BaseActivityimport com.fidilaundry.app.util.LoadingDialogimport com.fidilaundry.app.util.setSafeOnClickListenerimport org.koin.androidx.viewmodel.ext.android.getViewModelclass AddUserActivity : BaseActivity() {    lateinit var paperPrefs: PaperPrefs    lateinit var loadingDialog: LoadingDialog    private val binding: ActivityAddUserBinding by binding(R.layout.activity_add_user)    private val viewModel: HomeViewModel by lazy {        getViewModel(HomeViewModel::class)    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        binding.apply {            lifecycleOwner = this@AddUserActivity            this.vm = viewModel        }        var type = intent.getStringExtra("type")        when (type) {            "1" -> binding.tvTitle.text = "Tambah Pelanggan"            "2" -> binding.tvTitle.text = "Ubah Pelanggan"        }        binding.ivBack.setSafeOnClickListener {            finish()        }    }}