package com.fidilaundry.app.ui.home.master.adapterimport android.content.Contextimport android.content.Intentimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.core.content.res.ResourcesCompatimport androidx.recyclerview.widget.RecyclerViewimport com.fidilaundry.app.Rimport com.fidilaundry.app.basearch.localpref.PaperPrefsimport com.fidilaundry.app.databinding.ItemMasterMenuBindingimport com.fidilaundry.app.databinding.ItemUserBindingimport com.fidilaundry.app.ui.home.master.ItemListActivityimport com.fidilaundry.app.ui.home.master.MasterActivityimport com.fidilaundry.app.ui.home.master.PriceListActivityimport com.fidilaundry.app.ui.home.master.UserListActivityimport com.fidilaundry.app.ui.home.master.model.MasterMenuimport com.fidilaundry.app.util.setSafeOnClickListenerimport java.util.*class MasterMenuAdapter(private val context: Context?) : RecyclerView.Adapter<MasterMenuAdapter.ViewHolder>() {    private var appList: List<MasterMenu>    lateinit var paperPrefs: PaperPrefs    fun updateList(appList: List<MasterMenu>) {        this.appList = appList        notifyDataSetChanged()    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {        val binding = ItemMasterMenuBinding.inflate(LayoutInflater.from(parent.context), parent, false)        paperPrefs = PaperPrefs(this.context!!)        return ViewHolder(binding)    }    override fun onBindViewHolder(holder: ViewHolder, position: Int) {        holder.onBind(position)    }    override fun getItemCount(): Int {        return appList.size    }    inner class ViewHolder(private val binding: ItemMasterMenuBinding) : RecyclerView.ViewHolder(binding.root) {        fun onBind(position: Int) {            var app = appList[position]            binding.tvTitle.text = app.title            binding.cv.setSafeOnClickListener {                var intent: Intent? = null                when (app.id) {                    1 -> {                        intent = Intent(context, ItemListActivity::class.java)                        intent.putExtra("type", "category")                    }                    2 -> {                        intent = Intent(context, ItemListActivity::class.java)                        intent.putExtra("type", "service")                    }//                    2 -> Intent(context, ItemListActivity::class.java)//                    3 -> Intent(context, ItemListActivity::class.java)//                    4 -> Intent(context, ItemListActivity::class.java)                    3 -> intent = Intent(context, PriceListActivity::class.java)                    else -> intent = Intent(context, UserListActivity::class.java)                }                context?.startActivity(intent)            }        }    }    init {        appList = ArrayList()    }}