package com.fidilaundry.app.ui.home.master.adapterimport android.content.Contextimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.core.content.res.ResourcesCompatimport androidx.recyclerview.widget.RecyclerViewimport com.fidilaundry.app.Rimport com.fidilaundry.app.basearch.localpref.PaperPrefsimport com.fidilaundry.app.databinding.ItemPriceBindingimport com.fidilaundry.app.databinding.ItemUserBindingimport java.util.*class PriceListAdapter(private val context: Context?) : RecyclerView.Adapter<PriceListAdapter.ViewHolder>() {    private var appList: List<String>    lateinit var paperPrefs: PaperPrefs    fun updateList(appList: List<String>) {        this.appList = appList        notifyDataSetChanged()    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {        val binding = ItemPriceBinding.inflate(LayoutInflater.from(parent.context), parent, false)        paperPrefs = PaperPrefs(this.context!!)        return ViewHolder(binding)    }    override fun onBindViewHolder(holder: ViewHolder, position: Int) {        holder.onBind(position)    }    override fun getItemCount(): Int {        return appList.size    }    inner class ViewHolder(private val binding: ItemPriceBinding) : RecyclerView.ViewHolder(binding.root) {        fun onBind(position: Int) {            var app = appList[position]            binding.tvType.text = app        }    }    init {        appList = ArrayList()    }}