package com.fidilaundry.app.ui.home.orderimport android.os.Bundleimport androidx.recyclerview.widget.LinearLayoutManagerimport com.fidilaundry.app.Rimport com.fidilaundry.app.basearch.localpref.PaperPrefsimport com.fidilaundry.app.basearch.viewmodel.HomeViewModelimport com.fidilaundry.app.basearch.viewmodel.OrderViewModelimport com.fidilaundry.app.databinding.ActivityOrderAdminBindingimport com.fidilaundry.app.databinding.ActivityOrderUserBindingimport com.fidilaundry.app.ui.base.BaseActivityimport com.fidilaundry.app.ui.home.master.adapter.PriceListAdapterimport com.fidilaundry.app.ui.home.order.interfaces.IFItemClickimport com.fidilaundry.app.util.ListDivideritemDecorationimport com.fidilaundry.app.util.LoadingDialogimport com.fidilaundry.app.util.ScrollingLinearLayoutManagerimport com.fidilaundry.app.util.dialog.DialogAddDataimport com.fidilaundry.app.util.dialog.DialogServiceimport com.fidilaundry.app.util.fdialog.ConfirmMessageimport com.fidilaundry.app.util.fdialog.FGCallbackimport com.fidilaundry.app.util.setSafeOnClickListenerimport org.koin.androidx.viewmodel.ext.android.getViewModelimport java.util.ArrayListclass AdminOrderActivity : BaseActivity(), IFItemClick {    lateinit var paperPrefs: PaperPrefs    lateinit var loadingDialog: LoadingDialog    private var typeForm = 0    private val binding: ActivityOrderAdminBinding by binding(R.layout.activity_order_admin)    private val viewModel: OrderViewModel by lazy {        getViewModel(OrderViewModel::class)    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        binding.apply {            lifecycleOwner = this@AdminOrderActivity            this.vm = viewModel        }        var adapter = PriceListAdapter(this)        binding.rvSatuan.layoutManager =            ScrollingLinearLayoutManager(this, LinearLayoutManager.VERTICAL, false, 5000)        binding.rvSatuan.adapter = adapter        binding.rvSatuan.addItemDecoration(ListDivideritemDecoration(this))//        adapter.updateList(appList)        binding.btnOrder.setSafeOnClickListener {            dialogConfirm()        }        binding.ivBack.setSafeOnClickListener {            finish()        }        binding.etCategory.setSafeOnClickListener {            typeForm = 1            val myRoundedBottomSheet = DialogService(1, this)            myRoundedBottomSheet.show(supportFragmentManager, myRoundedBottomSheet.tag)        }        binding.etService.setSafeOnClickListener {            typeForm = 2            val myRoundedBottomSheet = DialogService(1, this)            myRoundedBottomSheet.show(supportFragmentManager, myRoundedBottomSheet.tag)        }    }    private val appList: List<String>        get() {            val appList: MutableList<String> = ArrayList()            appList.add("Selimut")            appList.add("Bed Cover")            appList.add("Boneka")            return appList        }    private fun dialogConfirm() {        ConfirmMessage(            this, "Apakah rincian pesanan Anda sudah benar?",            "", "", "Lanjutkan", "Batal",            object : FGCallback {                override fun onCallback() {                }            }        )    }    override fun onItemSelected(value: String?, id: String) {    }    override fun onItemSelected(value: String?, id: String, type: Int) {        when (typeForm) {            1 -> viewModel.categoryId.value = id            2 -> viewModel.serviceId.value = id        }    }}