package com.fidilaundry.app.ui.home.orderimport android.os.Bundleimport android.view.Viewimport androidx.lifecycle.Observerimport androidx.recyclerview.widget.LinearLayoutManagerimport com.fidilaundry.app.Rimport com.fidilaundry.app.basearch.localpref.PaperPrefsimport com.fidilaundry.app.basearch.viewmodel.HomeViewModelimport com.fidilaundry.app.basearch.viewmodel.OrderViewModelimport com.fidilaundry.app.databinding.ActivityOrderAdminBindingimport com.fidilaundry.app.databinding.ActivityOrderDetailBindingimport com.fidilaundry.app.databinding.ActivityOrderListBindingimport com.fidilaundry.app.databinding.ActivityOrderUserBindingimport com.fidilaundry.app.model.response.ItemListResponseimport com.fidilaundry.app.model.response.OrderDetailResponseimport com.fidilaundry.app.model.response.ProfileResponseimport com.fidilaundry.app.ui.base.BaseActivityimport com.fidilaundry.app.ui.home.master.adapter.ItemListAdapterimport com.fidilaundry.app.ui.home.master.adapter.PriceListAdapterimport com.fidilaundry.app.ui.home.master.model.DropdownItemimport com.fidilaundry.app.ui.home.order.adapter.ConfirmOrderAdapterimport com.fidilaundry.app.ui.home.order.adapter.SatuanAdapterimport com.fidilaundry.app.ui.home.order.adapter.StatusOrderAdapterimport com.fidilaundry.app.ui.home.order.interfaces.IFItemClickimport com.fidilaundry.app.ui.home.order.interfaces.IFSatuanimport com.fidilaundry.app.ui.home.order.model.SelectedSatuanItemimport com.fidilaundry.app.ui.home.order.model.StatusItemimport com.fidilaundry.app.util.*import com.fidilaundry.app.util.dialog.DialogAddDataimport com.fidilaundry.app.util.dialog.DialogConfirmOrderimport com.fidilaundry.app.util.dialog.DialogServiceimport com.fidilaundry.app.util.dialog.DialogShowQRimport com.fidilaundry.app.util.fdialog.ConfirmMessageimport com.fidilaundry.app.util.fdialog.ErrorMessageimport com.fidilaundry.app.util.fdialog.FGCallbackimport org.koin.androidx.viewmodel.ext.android.getViewModelimport java.util.ArrayListimport javax.net.ssl.SSLEngineResult.Statusclass OrderDetailActivity : BaseActivity(), IFItemClick {    lateinit var loadingDialog: LoadingDialog    private var adapter: StatusOrderAdapter? = null    private var profileData: ProfileResponse.Results? = null    private var transId: String = ""    private val binding: ActivityOrderDetailBinding by binding(R.layout.activity_order_detail)    private val viewModel: OrderViewModel by lazy {        getViewModel(OrderViewModel::class)    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        binding.apply {            lifecycleOwner = this@OrderDetailActivity            this.vm = viewModel        }        loadingDialog = LoadingDialog()        transId = intent.getStringExtra("transId").toString()        initViewModel()        adapter = StatusOrderAdapter(this, this)        binding.rvStatus.layoutManager =            ScrollingLinearLayoutManager(this, LinearLayoutManager.VERTICAL, false, 5000)        binding.rvStatus.adapter = adapter        adapter?.updateList(ctgList)        profileData = paperPref.getDataProfile()        if (profileData?.role == "customer" || profileData?.role == "member") {            layoutUser()        } else {            layoutAdmin()        }        binding.ivBack.setSafeOnClickListener {            finish()        }    }    private fun layoutUser() {        binding.btnComplaint.visibility = View.VISIBLE        binding.btnUpdateStatus.visibility = View.GONE    }    private fun layoutAdmin() {        binding.btnComplaint.visibility = View.GONE        binding.btnUpdateStatus.visibility = View.VISIBLE    }    private val ctgList: List<StatusItem>        get() {            val appList: MutableList<StatusItem> = ArrayList()            appList.add(StatusItem(2, "Dijemput"))            appList.add(StatusItem(3, "Pengecekan Baju"))            appList.add(StatusItem(4, "Pengerjaan"))            appList.add(StatusItem(5, "Selesai"))            appList.add(StatusItem(6, "Diantar"))            appList.add(StatusItem(7, "Komplain"))            return appList        }    private fun initViewModel() {        viewModel.orderDetailResponse.observe(this, Observer {            handleWhenDetailSuccess(it)        })        viewModel.showProgressLiveData.observe(this, Observer { showLoading ->            if (showLoading) {                if(loadingDialog != null){                    if(!loadingDialog.isShowLoad())                        loadingDialog.showProgressDialog(this, "Mohon tunggu…")                    else {                        loadingDialog.dismissDialog()                        loadingDialog.showProgressDialog(this, "Mohon tunggu…")                    }                }            } else {                loadingDialog.dismissDialog()            }        })        viewModel.showError.observe(this, Observer { showError ->            ErrorMessage(this, "", showError)        })    }    private fun handleWhenDetailSuccess(it: OrderDetailResponse?) {        var dt = it?.results!!        binding.tvOrderId.text = dt.id.toString()        binding.tvDate.text = DateTimeFormater(dt.createdAt!!)        binding.tvAddress.text = dt.addressDescription    }    override fun onResume() {        super.onResume()        viewModel.getOrderDetail(transId)    }    override fun onItemClick() {//        val myRoundedBottomSheet = DialogConfirmOrder()//        myRoundedBottomSheet.show(supportFragmentManager, myRoundedBottomSheet.tag)    }    override fun onItemSelected(value: String?, id: String) {    }    override fun onItemSelected(value: String?, id: String, type: Int) {    }}