package com.fidilaundry.app.ui.home.order.adapterimport android.content.Contextimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.fidilaundry.app.basearch.localpref.PaperPrefsimport com.fidilaundry.app.databinding.ItemSatuanDetailBindingimport com.fidilaundry.app.model.response.OrderDetailResponseimport com.fidilaundry.app.ui.home.order.interfaces.IFItemClickimport com.fidilaundry.app.util.RupiahCurrencyimport java.util.*class SatuanDetailAdapter(    private val context: Context?,    private val inf: IFItemClick) : RecyclerView.Adapter<SatuanDetailAdapter.ViewHolder>() {    private var appList: List<OrderDetailResponse.OrderItem>    lateinit var paperPrefs: PaperPrefs    private var selectedVal = ""    fun updateList(appList: List<OrderDetailResponse.OrderItem>) {        this.appList = appList        notifyDataSetChanged()    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {        val binding = ItemSatuanDetailBinding.inflate(LayoutInflater.from(parent.context), parent, false)        paperPrefs = PaperPrefs(this.context!!)        return ViewHolder(binding)    }    override fun onBindViewHolder(holder: ViewHolder, position: Int) {        holder.onBind(position)    }    override fun getItemCount(): Int {        return appList.size    }    inner class ViewHolder(private val binding: ItemSatuanDetailBinding) :        RecyclerView.ViewHolder(binding.root) {        fun onBind(position: Int) {            var app = appList[position]            binding.tvQty.text = "${app.qty.toString()} x "            binding.tvPrice.text = "${RupiahCurrency.Converter(app.item?.price?.toDouble())}"            binding.tvType.text = "(${app.item?.title})"            binding.tvTotal.text = RupiahCurrency.Converter(app.amount?.toDouble())        }    }    init {        appList = ArrayList()    }}