package com.fidilaundry.app.utilimport java.math.RoundingModeimport java.text.DecimalFormatimport java.text.DecimalFormatSymbolsimport java.text.NumberFormatobject RupiahCurrency {    fun Converter(value: Double?): String {        val kursIndonesia: DecimalFormat = DecimalFormat.getCurrencyInstance() as DecimalFormat        val formatRp = DecimalFormatSymbols()        formatRp.currencySymbol = "Rp. "        formatRp.monetaryDecimalSeparator = ','        formatRp.groupingSeparator = '.'        kursIndonesia.decimalFormatSymbols = formatRp        kursIndonesia.minimumFractionDigits = 0        kursIndonesia.maximumFractionDigits = 0        return kursIndonesia.format(value)    }    fun removeCurrency(value: String): Double {        var newVar = value.split(",")[0]        newVar = newVar.replace("Rp. ","")        newVar = newVar.replace(".","")        newVar = newVar.replace(",","")        return NumberFormat.getInstance().parse(newVar).toDouble()    }}