package com.fidilaundry.app.utilimport java.math.RoundingModeimport java.text.DecimalFormatimport java.text.DecimalFormatSymbolsimport java.text.NumberFormatobject StatusHelper {    fun setStatus(statBefore: String, tfMethod: String = ""): String {        var result = ""        when (statBefore) {            "pending" -> result = "dijemput"            "dijemput" -> result = "cek item"            "cek item" -> result = "pengerjaan"            "pengerjaan" -> result = "selesai-pengerjaan"            "selesai-pengerjaan" -> result = "menunggu-pembayaran"            "menunggu-pembayaran" -> result = "pembayaran-sukses"            "pembayaran-sukses" -> {                if (tfMethod == "Antar Jemput") {                    result = "antar"                } else {                    result = "selesai"                }            }            "antar" -> result = "selesai"            "selesai" -> result = "komplain"            "komplain" -> result = "solve"        }        return result    }    fun setStatusName(statBefore: String): String {        var result = ""        when (statBefore) {            "pending" -> result = "Pending"            "dijemput" -> result = "Dijemput"            "cek item" -> result = "Pengecekan Baju"            "pengerjaan" -> result = "Pengerjaan"            "selesai-pengerjaan" -> result = "Pengerjaan Selesai"            "antar" -> result = "Diantar"            "selesai" -> result = "Selesai"            "menunggu-pembayaran" -> result = "Menunggu Pembayaran"            "pembayaran-sukses" -> result = "Pembayaran Selesai"            "komplain" -> result = "Komplain"            "solve" -> result = "Komplain Terselesaikan"            else -> result = statBefore        }        return result    }}