package com.fidilaundry.app.utilimport android.text.TextUtilsimport android.util.Logimport android.util.Patternsimport java.util.regex.Patternobject TextCheckerFormater {    fun isValidEmail(target: CharSequence?): Boolean {        val EMAIL_PATTERN =            "^[_A-Za-z0-9-]+(\\.[_A-Za-z0-9-]+)*@[A-Za-z0-9]+(\\.[A-Za-z0-9]+)*(\\.[A-Za-z]{2,})$"        var pattern = Pattern.compile(EMAIL_PATTERN)        var matcher = pattern.matcher(target)        return matcher.matches()//        return if (TextUtils.isEmpty(target)) {//            false//        } else {//            Patterns.EMAIL_ADDRESS.matcher(target!!).matches()//        }    }    fun isValidPhoneNumber(target: CharSequence?): Boolean {        return if (target == null || target.length < 11 || target.length > 14) {            false        } else {            Patterns.PHONE.matcher(target).matches()        }    }    fun isValidPhoneFormat(target: CharSequence?): Boolean {        return target.toString().substring(0, 2) == "08"    }    fun isValidKtp(target: CharSequence?): Boolean {        return !(target == null || target.length < 16)    }    fun isValidUsernameLength(target: CharSequence?): Boolean {        return !(target == null || target.length < 6)    }    fun isValidUsername(target: CharSequence?): Boolean {        val U_PATTERN = "^[a-zA-Z0-9_.]*$"        var pattern = Pattern.compile(U_PATTERN)        var matcher = pattern.matcher(target)        return matcher.matches()    }    fun isValidFullname(target: CharSequence?): Boolean {        return !(target == null || target.length < 6)    }    fun isValidPasswordLength(target: CharSequence?): Boolean {        return !(target == null || target.length < 8)    }    fun isValidPassword(target: CharSequence?): Boolean {        val PASS_PATTERN =            "^(?=.*[0-9])(?=.*[a-zA-Z])(?=\\S+\$).{8,20}\$"        var pattern = Pattern.compile(PASS_PATTERN)        var matcher = pattern.matcher(target)        return matcher.matches()//        return if (TextUtils.isEmpty(target)) {//            false//        } else {//            Patterns.EMAIL_ADDRESS.matcher(target!!).matches()//        }    }}