package com.fidilaundry.app.utilimport android.os.SystemClockimport android.view.*import androidx.annotation.LayoutResfun inflateView(@LayoutRes layoutResId: Int, parent: ViewGroup, attachToRoot: Boolean): View {    return LayoutInflater.from(parent.context).inflate(layoutResId, parent, attachToRoot)}inline fun <T:Any, R> whenNotNull(input: T?, callback: (T)->R): R? {    return input?.let(callback)}infix fun <T> Boolean.then(param: T): T? = if(this) param else nullclass SafeClickListener(        private val interval: Int = 500,        private val onSafeClick: (View) -> Unit) : View.OnClickListener {    private var lastTimeClicked: Long = 0    override fun onClick(v: View) {        if (SystemClock.elapsedRealtime() - lastTimeClicked < interval) {            return        }        lastTimeClicked = SystemClock.elapsedRealtime()        onSafeClick(v)    }}fun View.setSafeOnClickListener(onSafeClick: (View) -> Unit) {    val safeClickListener = SafeClickListener {        onSafeClick(it)    }    setOnClickListener(safeClickListener)}